    private void btn_calcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_calcularActionPerformed
        try{
            Registry registro = LocateRegistry.getRegistry("127.0.0.1", 9999);
            IRMI interfaz = (IRMI)registro.lookup("mates");
            
            int oper1; //variable operacion numerador
            int oper2; //variable operacion denominador
            float result; //variable resultado num / dem
            int a, b, c, d; //valores de cada textfield
            a = Integer.parseInt(txt_num1.getText());
            b = Integer.parseInt(txt_num2.getText());
            c = Integer.parseInt(txt_num3.getText());
            d = Integer.parseInt(txt_num4.getText());
            
            oper1 = interfaz.sumar1(a, b, c, d); //operacion numerador
            oper2 = interfaz.sumar2(b, d); //operacion denominador
            
            result = oper1/oper2; //operacion decimal
            
            String resultado1 = String.valueOf(oper1); //conversion a string
            txt_salida1.setText(resultado1); //colocar dato en textfield
            String resultado2 = String.valueOf(oper2);
            txt_salida2.setText(resultado2);
            String resultado3 = String.valueOf(result);
            txt_salir.setText(resultado3);
        }
        catch(RemoteException | NotBoundException | NumberFormatException rex){
            JOptionPane.showMessageDialog(this,rex.getMessage());
        }
    }//GEN-LAST:event_btn_calcularActionPerformed

